import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { 
  Plus, Edit, Trash2, Eye, EyeOff, LogOut, 
  Search, Filter, ArrowLeft, Save, X 
} from 'lucide-react';
import { useSaloes, Salao } from '../contexts/SaloesContext';

const AdminDashboard: React.FC = () => {
  const { saloes, addSalao, updateSalao, deleteSalao, toggleSalaoStatus } = useSaloes();
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingSalao, setEditingSalao] = useState<Salao | null>(null);
  const [formData, setFormData] = useState<Partial<Salao>>({});
  const navigate = useNavigate();

  useEffect(() => {
    const auth = localStorage.getItem('adminAuthenticated');
    if (!auth) {
      navigate('/admin');
      return;
    }
    setIsAuthenticated(true);
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('adminAuthenticated');
    navigate('/admin');
  };

  const handleAddSalao = () => {
    if (formData.nome && formData.endereco && formData.telefone) {
      addSalao({
        nome: formData.nome,
        endereco: formData.endereco,
        telefone: formData.telefone,
        email: formData.email || '',
        logo: formData.logo || '',
        siteUrl: formData.siteUrl || '',
        horarioFuncionamento: formData.horarioFuncionamento || '',
        servicos: formData.servicos || [],
        descricao: formData.descricao || '',
        ativo: true,
      });
      setFormData({});
      setShowAddForm(false);
    }
  };

  const handleUpdateSalao = () => {
    if (editingSalao && formData.nome) {
      updateSalao(editingSalao.id, formData);
      setEditingSalao(null);
      setFormData({});
    }
  };

  const handleDeleteSalao = (id: string) => {
    if (window.confirm('Tem certeza que deseja excluir este salÃ£o?')) {
      deleteSalao(id);
    }
  };

  const filteredSaloes = saloes.filter(salao =>
    salao.nome.toLowerCase().includes(searchTerm.toLowerCase()) ||
    salao.endereco.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (!isAuthenticated) {
    return null;
  }

  return (
    <DashboardContainer>
      <Header>
        <HeaderContent>
          <BackButton onClick={() => navigate('/')}>
            <ArrowLeft size={20} />
            Voltar ao Site
          </BackButton>
          
          <HeaderTitle>Painel Administrativo</HeaderTitle>
          
          <LogoutButton onClick={handleLogout}>
            <LogOut size={20} />
            Sair
          </LogoutButton>
        </HeaderContent>
      </Header>

      <MainContent>
        <ControlsSection>
          <SearchContainer>
            <Search size={20} />
            <SearchInput
              type="text"
              placeholder="Buscar salÃµes..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </SearchContainer>

          <AddButton
            onClick={() => setShowAddForm(true)}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <Plus size={20} />
            Adicionar SalÃ£o
          </AddButton>
        </ControlsSection>

        {showAddForm && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
          >
            <FormCard>
              <FormTitle>Adicionar Novo SalÃ£o</FormTitle>
              <FormGrid>
                <FormField>
                  <Label>Nome do SalÃ£o *</Label>
                  <Input
                    type="text"
                    value={formData.nome || ''}
                    onChange={(e) => setFormData({ ...formData, nome: e.target.value })}
                    placeholder="Nome do salÃ£o"
                  />
                </FormField>
                
                <FormField>
                  <Label>EndereÃ§o *</Label>
                  <Input
                    type="text"
                    value={formData.endereco || ''}
                    onChange={(e) => setFormData({ ...formData, endereco: e.target.value })}
                    placeholder="EndereÃ§o completo"
                  />
                </FormField>
                
                <FormField>
                  <Label>Telefone *</Label>
                  <Input
                    type="text"
                    value={formData.telefone || ''}
                    onChange={(e) => setFormData({ ...formData, telefone: e.target.value })}
                    placeholder="(11) 99999-9999"
                  />
                </FormField>
                
                <FormField>
                  <Label>Email</Label>
                  <Input
                    type="email"
                    value={formData.email || ''}
                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                    placeholder="email@salao.com"
                  />
                </FormField>
                
                <FormField>
                  <Label>URL do Site</Label>
                  <Input
                    type="url"
                    value={formData.siteUrl || ''}
                    onChange={(e) => setFormData({ ...formData, siteUrl: e.target.value })}
