import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { ArrowLeft, Send, User, Mail, Phone, MapPin, MessageSquare } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

interface FormData {
  nome: string;
  email: string;
  telefone: string;
  endereco: string;
  mensagem: string;
}

const CentralAtendimento: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState<FormData>({
    nome: '',
    email: '',
    telefone: '',
    endereco: '',
    mensagem: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.nome || !formData.email || !formData.telefone) {
      alert('Por favor, preencha todos os campos obrigatórios.');
      return;
    }

    setIsSubmitting(true);

    // Simular envio de email (aqui você integraria com um serviço real)
    try {
      // Aqui você faria a integração com um serviço de email como EmailJS
      console.log('Dados do cliente:', formData);

      // Simular delay de envio
      await new Promise(resolve => setTimeout(resolve, 2000));

      setSubmitted(true);
      setFormData({
        nome: '',
        email: '',
        telefone: '',
        endereco: '',
        mensagem: ''
      });
    } catch (error) {
      console.error('Erro ao enviar:', error);
      alert('Erro ao enviar cadastro. Tente novamente.');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (submitted) {
    return (
      <Container>
        <SuccessCard
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5 }}
        >
          <SuccessIcon>✓</SuccessIcon>
          <SuccessTitle>Cadastro Enviado com Sucesso!</SuccessTitle>
          <SuccessMessage>
            Obrigado pelo seu interesse! Recebemos seu cadastro e entraremos em contato em breve.
          </SuccessMessage>
          <BackButton onClick={() => {
            setSubmitted(false);
            navigate('/');
          }}>
            <ArrowLeft size={20} />
            Voltar ao Site
          </BackButton>
        </SuccessCard>
      </Container>
    );
  }

  return (
    <Container>
      <Header>
        <BackButton onClick={() => navigate('/')}>
          <ArrowLeft size={20} />
          Voltar ao Site
        </BackButton>
        <Title>Central de Atendimento</Title>
        <Subtitle>Cadastre-se para receber informações sobre nossos salões</Subtitle>
      </Header>

      <FormCard
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        <Form onSubmit={handleSubmit}>
          <FormGrid>
            <FormField>
              <Label>
                <User size={18} />
                Nome Completo *
              </Label>
              <Input
                type="text"
                value={formData.nome}
                onChange={(e) => setFormData({ ...formData, nome: e.target.value })}
                placeholder="Seu nome completo"
                required
              />
            </FormField>

            <FormField>
              <Label>
                <Mail size={18} />
                Email *
              </Label>
              <Input
                type="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                placeholder="seu@email.com"
                required
              />
            </FormField>

            <FormField>
              <Label>
                <Phone size={18} />
                Telefone *
              </Label>
              <Input
                type="tel"
                value={formData.telefone}
                onChange={(e) => setFormData({ ...formData, telefone: e.target.value })}
                placeholder="(11) 99999-9999"
                required
              />
            </FormField>

            <FormField>
              <Label>
                <MapPin size={18} />
                Endereço
              </Label>
              <Input
                type="text"
                value={formData.endereco}
                onChange={(e) => setFormData({ ...formData, endereco: e.target.value })}
                placeholder="Seu endereço (opcional)"
              />
            </FormField>

            <FormField fullWidth>
              <Label>
                <MessageSquare size={18} />
                Mensagem
              </Label>
              <Textarea
                value={formData.mensagem}
                onChange={(e) => setFormData({ ...formData, mensagem: e.target.value })}
                placeholder="Conte-nos sobre seus interesses ou dúvidas..."
                rows={4}
              />
            </FormField>
          </FormGrid>

          <SubmitButton
            type="submit"
            disabled={isSubmitting}
            whileHover={{ scale: isSubmitting ? 1 : 1.02 }}
            whileTap={{ scale: isSubmitting ? 1 : 0.98 }}
          >
            {isSubmitting ? (
              <>
                <LoadingSpinner />
                Enviando...
              </>
            ) : (
              <>
                <Send size={20} />
                Enviar Cadastro
              </>
            )}
          </SubmitButton>
        </Form>
      </FormCard>
    </Container>
  );
};

