  background: ${({ theme }) => theme.colors.textLight};
  color: white;
  border: none;
  padding: ${({ theme }) => theme.spacing.md} ${({ theme }) => theme.spacing.lg};
  border-radius: ${({ theme }) => theme.borderRadius.medium};
  font-size: 1.6rem;
  font-weight: 600;
  display: flex;
  align-items: center;
