import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Search, X } from 'lucide-react';
import { useSaloes } from '../contexts/SaloesContext';
import Header from '../components/Header';
import SalaoCard from '../components/SalaoCard';
import AdminButton from '../components/AdminButton';

const HomePage: React.FC = () => {
  const { saloes } = useSaloes();
  const saloesAtivos = saloes.filter(salao => salao.ativo);
  const [showSearchPopup, setShowSearchPopup] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredSaloes, setFilteredSaloes] = useState(saloesAtivos);

  const handleSearch = (term: string) => {
    setSearchTerm(term);
    if (term.trim() === '') {
      setFilteredSaloes(saloesAtivos);
    } else {
      const filtered = saloesAtivos.filter(salao => 
        salao.endereco.toLowerCase().includes(term.toLowerCase()) ||
        salao.nome.toLowerCase().includes(term.toLowerCase())
      );
      setFilteredSaloes(filtered);
    }
  };

  const openSearchPopup = () => {
    setShowSearchPopup(true);
    setFilteredSaloes(saloesAtivos);
  };

  const closeSearchPopup = () => {
    setShowSearchPopup(false);
    setSearchTerm('');
    setFilteredSaloes(saloesAtivos);
  };

  return (
    <PageContainer>
      <Header />
      
      <HeroSection>
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <HeroTitle>SalÃµes Online</HeroTitle>
          <HeroSubtitle>
            <SearchButton onClick={openSearchPopup}>
              <Search size={20} />
              Encontre o salÃ£o mais prÃ³ximo
            </SearchButton>
          </HeroSubtitle>
        </motion.div>
      </HeroSection>

      <MainContent>
        <SectionHeader>
          <SectionTitle>Nossos SalÃµes</SectionTitle>
        </SectionHeader>

        {showSearchPopup && (
          <SearchPopupOverlay
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={closeSearchPopup}
          >
            <SearchPopupContent
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              <SearchPopupHeader>
                <SearchPopupTitle>Encontrar SalÃ£o por EndereÃ§o</SearchPopupTitle>
                <CloseButton onClick={closeSearchPopup}>
                  <X size={24} />
                </CloseButton>
              </SearchPopupHeader>
              
              <SearchInputContainer>
                <SearchIcon>
                  <Search size={20} />
                </SearchIcon>
                <SearchInput
                  type="text"
                  placeholder="Digite o endereÃ§o ou nome do salÃ£o..."
                  value={searchTerm}
                  onChange={(e) => handleSearch(e.target.value)}
                  autoFocus
                />
              </SearchInputContainer>
              
              <SearchResults>
                {filteredSaloes.length > 0 ? (
                  filteredSaloes.map((salao) => (
                    <SearchResultItem key={salao.id}>
                      <SalaoInfo>
                        <SalaoName>{salao.nome}</SalaoName>
                        <SalaoAddress>{salao.endereco}</SalaoAddress>
                      </SalaoInfo>
                      <SelectButton onClick={() => {
                        closeSearchPopup();
                        // Scroll to the salon card
                        const element = document.getElementById(`salao-${salao.id}`);
                        if (element) {
                          element.scrollIntoView({ behavior: 'smooth' });
                        }
                      }}>
                        Ver SalÃ£o
                      </SelectButton>
                    </SearchResultItem>
                  ))
                ) : (
                  <NoResults>
                    {searchTerm ? 'Nenhum salÃ£o encontrado para esta pesquisa.' : 'Digite para pesquisar...'}
                  </NoResults>
                )}
              </SearchResults>
            </SearchPopupContent>
